UPDATE REPORTWIZARD
SET SEGMENTATION_QUERY =
CONCAT('SELECT ', CAST(@REPORTWIZARD_ID AS CHAR(20)), ', CONSTITUENT.CONSTITUENT_ID
FROM
(
-- Constituent Segmentation based on Paid gifts updated today
SELECT DISTINCT
CONSTITUENT_ID
FROM GIFT
WHERE (CAST(UPDATE_DATE AS DATE) = DATE_ADD(CURDATE(),INTERVAL 0 DAY)) 
AND GIFT_STATUS = ''paid''

UNION

-- On Behalf of Constituents based on Paid gifts updated today
SELECT DISTINCT
CUSTOM_FIELD.FIELD_NUMERIC_VALUE AS CONSTITUENT_ID
FROM GIFT
JOIN DISTRO_LINE ON GIFT.GIFT_ID = DISTRO_LINE.GIFT_ID
JOIN CUSTOM_FIELD ON CUSTOM_FIELD.ENTITY_TYPE = ''distributionLine''
AND CUSTOM_FIELD.ENTITY_ID = DISTRO_LINE.DISTRO_LINE_ID
AND CUSTOM_FIELD.FIELD_NAME = ''onBehalfOf''
WHERE (CAST(UPDATE_DATE AS DATE) = DATE_ADD(CURDATE(),INTERVAL 0 DAY))
AND GIFT_STATUS = ''paid''

UNION

-- Constituents based on Received GIKs updated today
SELECT DISTINCT
CONSTITUENT_ID
FROM GIFT_IN_KIND 
WHERE GIFT_IN_KIND_STATUS = ''Received''
AND (CAST(UPDATE_DATE AS DATE) = DATE_ADD(CURDATE(),INTERVAL 0 DAY))

UNION

-- Constituents with Pledges updated today
SELECT DISTINCT
CONSTITUENT_ID
FROM PLEDGE
WHERE (CAST(UPDATE_DATE AS DATE) = DATE_ADD(CURDATE(),INTERVAL 0 DAY))

UNION

-- Constituent Segmentation based on Recurring gifts updated today
SELECT DISTINCT
CONSTITUENT_ID
FROM RECURRING_GIFT
WHERE (CAST(UPDATE_DATE AS DATE) = DATE_ADD(CURDATE(),INTERVAL 0 DAY))
) AS CONSTITUENTS_TO_ROLLUP
JOIN CONSTITUENT ON CONSTITUENTS_TO_ROLLUP.CONSTITUENT_ID = CONSTITUENT.CONSTITUENT_ID
WHERE IS_DELETED = 0;')
WHERE REPORTWIZARD_ID = @REPORTWIZARD_ID;
 

